if __name__ == '__main__':
  log = Logger('WRM', base_folder='.', app_folder='output')
  
  from utils.data.loaders import DEFAULT_WARMUP
  
  CT_TIME = 7
  
  lst_data = []
  n_obs = 0
  
  for categ in BASIC_DATA:
    recs = BASIC_DATA[categ]
    if len(recs) == 0:
      continue
    log.P("Processing '{}' with {} obs".format(categ, len(recs)))
    n_obs += len(recs)
    for rec in recs:
      rec['completion'] = '__' in rec['q']
      rec['category'] = categ
      rec['question'] = rec.pop('q')
      rec['answer'] = rec.pop('a')
      rec['options'] = rec.pop('wa')
      rec['language'] = rec.pop('l')
      if 'time' not in rec:
        rec['time'] = CT_TIME
      lst_data.append(rec)
  log.P("Total {} observations".format(n_obs))
        
  
  df = pd.DataFrame(lst_data)
  FN = './utils/data/warmup0.txt'
  n_local = df.shape[0]
  n_external = 0
  if os.path.isfile(FN):
    df0 = pd.read_pickle(FN, compression='zip')
    n_external = df0.shape[0]
    df = pd.concat((df, df0))
  df.reset_index(drop=True, inplace=True)
  n_obs = df.shape[0]
  log.P("Output {} generated from local {} and {} external, ro:{}, en:{}, columns: {}".format(
    df.shape, n_local, n_external, 
    (df.language == 'ro').sum(), (df.language == 'en').sum(),
    list(df.columns),
  ))
  dups = df.duplicated(subset='question', keep=False)
  if len(dups) > 0:
    log.P("Found duplicates:\n{}".format(df[dups][['question', 'answer']]))
    df.drop_duplicates(subset='question', inplace=True)
    log.P("After remove duplicates: {} vs initial {}".format(df.shape[0], n_obs))
    df.reset_index(drop=True, inplace=True)
  
  df.to_pickle(DEFAULT_WARMUP, compression='zip')
  
  
